import styled from 'styled-components'

export const HomeApp = styled.section`
    float: left;
    width: 100%;
`;

export const HomeTop = styled.div`
    float: left;
    width: 100%;
    margin-top: 64px;
    display: flex;
    align-items: center;

    .home-top-child {
        float: left;
    }

    .home-top-first {
        width: 272px;
    }

    .home-top-first img {
        width: 100% ;
    }

    .home-top-second {
        width: 472px;
        margin: 0 40px;
    }

    .product-categorie {
        float: left;
        width: 100%;
        font-family: SFProText;
        font-size: 12px;
        font-weight: bold;
        font-style: normal;
        font-stretch: normal;
        line-height: normal;
        letter-spacing: 0.3px;
        color: rgba(0, 0, 0, 0.38);
    }

    .product-name {
        float: left;
        width: 100%;
        margin: 11px 0 15px;
        font-family: SFProDisplay;
        font-size: 38px;
        font-weight: 900;
        font-style: normal;
        font-stretch: normal;
        line-height: 1.29;
        letter-spacing: normal;
        color: rgba(0, 0, 0, 0.87);
    }

    .product-infos {
        float: left;
        width: 100%;
    }

    .product-infos .product-info-image {
        width: 112px;
        float: left;
    }

    .product-infos .product-info-date {
        float: left;
        height: 20px;
        margin-left: 15px;
    }

    .product-infos .product-info-date p {
        float: left;
        color: #1e88e5;
        font-size: 12px;
        text-transform: uppercase;
        font-family: SFProText;
        font-weight: 500;
        font-style: normal;
        font-stretch: normal;
        line-height: 20px;
        letter-spacing: 0.3px;
    }
`;

export const HomeBottom = styled.div`
    float: left;
    width: 100%;
    margin-top: 45px;

    .details {
        float: left;
        width: calc(100% - 392px);
    }

    .product-detail {
        float: left;
        width: 25%;
    }

    .product-detail-image {
        float: left;
        width: 32px;
        height: 32px;
        margin-right: 10px;
    }

    .product-detail-image img {
        width: 100%;
    }

    .product-detail-description {
        width: 144px;
        float: left;
        font-family: SFProText;
        font-size: 16px;
        font-weight: normal;
        font-style: normal;
        font-stretch: normal;
        line-height: 1.38;
        letter-spacing: normal;
    }

    .product-detail-description .detail-title {
        color: rgba(0, 0, 0, 0.87);
    }

    .product-detail-description .detail-info {
        color: rgba(0, 0, 0, 0.38);
    }

    .score {
        float: left;
        width: 392px;
        height: 82px;
        padding: 13px 24px;
        border-radius: 4px;
        border: solid 2px #f4f4f7;
        display: flex;
        align-items: center;
    }

    .score-image {
        float: left;
        width: 56px;
        height: 56px;
        margin-right: 24px;
    }

    .score-description {
        float: left;
    }

    .score-title {
        font-family: SFProText;
        font-size: 12px;
        font-weight: bold;
        font-style: normal;
        font-stretch: normal;
        line-height: normal;
        letter-spacing: 0.3px;
        text-transform: uppercase;
        color: rgba(0, 0, 0, 0.38);
    }

    .score-info {
        font-family: SFProText;
        font-size: 16px;
        font-weight: normal;
        font-style: normal;
        font-stretch: normal;
        line-height: 1.44;
        letter-spacing: normal;
    }

    .score-info strong {
        color: rgba(0, 0, 0, 0.6);
    }

    .score-info a {
        color: #1e88e5;
        text-decoration: none;
        letter-spacing: 1px;
    }
`;
