const size = {
    mobile: "500px",
    tablet: "978px",
    laptop: "1280px",
    desktop: "1440px"
};

export const device = {
    mobile: `(max-width: ${size.mobile})`,
    tablet: `(max-width: ${size.tablet})`,
    laptop: `(max-width: ${size.laptop})`,
    desktop: `(max-width: ${size.desktop})`
};
