import styled from 'styled-components';
import { device } from '../../Service/Device';

export const HomeApp = styled.section`
    float: left;
    width: 100%;
    padding-top: 70px;
`;

export const HomeAll = styled.div`
    float: left;
    width: calc(100% - 390px);
`;

export const HomeTop = styled.div`
    float: left;
    width: 100%;
    display: flex;
    align-items: center;

    .home-top-child {
        float: left;
    }

    .home-top-first {
        width: 270px;
    }

    .home-top-first img {
        width: 100% ;
    }

    .home-top-second {
        width: calc(100% - 270px);
        margin: 0 40px;
    }

    .product-categorie {
        float: left;
        width: 100%;
        font-family: SFProText;
        font-size: 12px;
        font-weight: bold;
        font-style: normal;
        font-stretch: normal;
        line-height: normal;
        letter-spacing: 0.3px;
        color: rgba(0, 0, 0, 0.38);
    }

    .product-name {
        float: left;
        width: 100%;
        margin: 11px 0 15px;
        font-family: SFProDisplay;
        font-size: 38px;
        font-weight: 900;
        font-style: normal;
        font-stretch: normal;
        line-height: 1.29;
        letter-spacing: normal;
        color: rgba(0, 0, 0, 0.87);
    }

    .product-infos {
        float: left;
        width: 100%; 

        .product-info-image {
            min-width: 10px;
            float: left;

            img {
                margin-right: 1px;
            }
        }

        .product-info-date {
            float: left;
            height: 20px;
            margin-left: 7px;

            p {
                float: left;
                color: #1e88e5;
                font-size: 12px;
                text-transform: uppercase;
                font-family: SFProText;
                font-weight: 500;
                font-style: normal;
                font-stretch: normal;
                line-height: 20px;
                letter-spacing: 0.3px;
            }
        }
    }

    @media ${device.mobile} {

        .home-top-first {
            width: 90%;
        }

        .home-top-second {
            width: 90%;
        }
    }
`;

export const HomeBottom = styled.div`
    float: left;
    width: 100%;
    margin-top: 45px;

    .details {
        float: left;
        width: 100%;
    }

    .product-detail {
        float: left;
        width: 25%;
    }

    .product-detail-image {
        float: left;
        width: 32px;
        height: 32px;
        margin-right: 10px;
    }

    .product-detail-image img {
        width: 100%;
    }

    .product-detail-description {
        width: 144px;
        float: left;
        font-family: SFProText;
        font-size: 16px;
        font-weight: normal;
        font-style: normal;
        font-stretch: normal;
        line-height: 1.38;
        letter-spacing: normal;
    }

    .product-detail-description .detail-title {
        color: rgba(0, 0, 0, 0.87);
    }

    .product-detail-description .detail-info {
        color: rgba(0, 0, 0, 0.38);
    }
`;
