import styled from 'styled-components'

export const DetailApp = styled.section`
    float: left;
    width: 100%;

    .home-bottom {
        float: left;
        width: 100%;
        margin-top: 45px;
    }

    .details {
        float: left;
        width: calc(100% - 392px);
    }

    .product-detail {
        float: left;
        width: 25%;
    }

    .product-detail-image {
        float: left;
        width: 32px;
        height: 32px;
        margin-right: 10px;
    }

    .product-detail-image img {
        width: 100%;
    }

    .product-detail-description {
        width: 144px;
        float: left;
        font-family: SFProText;
        font-size: 16px;
        font-weight: normal;
        font-style: normal;
        font-stretch: normal;
        line-height: 1.38;
        letter-spacing: normal;
    }

    .product-detail-description .detail-title {
        color: rgba(0, 0, 0, 0.87);
    }

    .product-detail-description .detail-info {
        color: rgba(0, 0, 0, 0.38);
    }

    .score {
        float: left;
        width: 392px;
        height: 82px;
        padding: 13px 24px;
        border-radius: 4px;
        border: solid 2px #f4f4f7;
        display: flex;
        align-items: center;
    }

    .score-image {
        float: left;
        width: 56px;
        height: 56px;
        margin-right: 24px;
    }

    .score-description {
        float: left;
    }

    .score-title {
        font-family: SFProText;
        font-size: 12px;
        font-weight: bold;
        font-style: normal;
        font-stretch: normal;
        line-height: normal;
        letter-spacing: 0.3px;
        text-transform: uppercase;
        color: rgba(0, 0, 0, 0.38);
    }

    .score-info {
        font-family: SFProText;
        font-size: 16px;
        font-weight: normal;
        font-style: normal;
        font-stretch: normal;
        line-height: 1.44;
        letter-spacing: normal;
    }

    .score-info strong {
        color: rgba(0, 0, 0, 0.6);
    }

    .score-info a {
        color: #1e88e5;
        text-decoration: none;
        letter-spacing: 1px;
    }
`;
