    }

    .header-bottom-menu {
        float: left;
        width: 100%;

        ul { 
            margin: 0;
            padding: 0;
            line-height: 64px;

            li {
                list-style: none;
                display: inline;
                margin-right: 30px;

                a { 
                    color: #1e88e5;
                    font-size: 12px;
                    text-decoration: none;
                    text-transform: uppercase;
                }
            }
        }
    }

    @media ${device.mobile} {

